import styled from "styled-components";
import { Parallax, Background } from 'react-parallax';

const ToolsWrapper = styled.div`
    width: 19rem;
    height: 19rem;
    display: flex;
    flex-direction: column;
    aligh-items: center;
    justify-content: center;
    margin: 1rem;

    background: #09273f;
    color: #ffffff;
    box-shadow: rgb(0 0 0 / 25%) 0px 3px 6px 0px;
    padding: 32px;
    border-radius: 8px;
    width: 100%;



    //syntax like in scss
    .icon-wrapper{
        width: 7rem;
        height: 7rem;
        margin: auto;
        border-radius: 50%;
        user-select: none;
        background-color: #398EEA;
        display:flex;
        align-items:center;
        justify-content:center;

    }

    h3 {
        text-align: center;
        color: #e5ff60;
        font-size:24px;
    }
`


const Tools = ({title, src, text}) => {
    return (
        <Parallax strength={300}>
        <Background className="custom-bg">
            <img src="https://media.istockphoto.com/photos/scotch-whiskey-bottle-glass-and-old-barrel-picture-id1299900776?b=1&k=20&m=1299900776&s=170667a&w=0&h=XGjQPmqYIrTJwobhTMcm-HX79pPLtgtWqWnKOi_Ns4c=" alt="fill murray" />
        <ToolsWrapper>
            <div className="icon-wrapper">{src}</div>
            <h3>{title}</h3>
            <p>{text}</p>
        </ToolsWrapper>
        </Background>
    </Parallax>

    )
}

export default Tools;
