import styled, { css } from "styled-components";
import { useEffect, useState } from "react";

import PostsContainer from "./PostsContainer/PostsContainer";

const StyledPosts = styled.div`
  img {
    max-width: 16rem;
    height: auto;
    margin: auto;
  }

  h5 {
    text-align: center;
    font-size: 18px;
    color: #0c2c00;
  }

  p {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
    text-align: justify;
    font-size: 16px;
    color: #0c2c00;

  }
`;
const RowContainer = styled(StyledPosts)`
  width: 19rem;
  height: 28rem;
  display: flex;
  flex-direction: column;
  aligh-items: center;
  justify-content: center;
  margin: 1rem;

  ${(props) =>
    props.primary &&
    css`
      width: 19rem;
      height: 28rem;
    `}
`;

const AllRecipes = () => {
  const [postData, setPostData] = useState(null);
  useEffect(() => {
    fetch("https://www.thecocktaildb.com/api/json/v1/1/search.php?s=margarita")
      .then((response) => response.json())
      .then((data) => setPostData(data))
      .catch(console.error("There's an error"));
  }, []);

  return (
    <PostsContainer>
      {postData ? (
        postData.drinks.map((post) => {
          return (
            <RowContainer primary key={post.idDrink}>
              <h5>{post.strDrink}</h5>
              <img src={post.strDrinkThumb} alt={post.strDrink} />
              <p>{post.strInstructions}</p>
            </RowContainer>
          );
        })
      ) : (
        <div>loading ...</div>
      )}
    </PostsContainer>
  );
};

export default AllRecipes;
